mapper.select:
select count(1) from (select * from t_media where film_name like #{want}
UNION select * from t_media where film_stars like #{want}
UNION select * from t_media where film_director like #{want}
UNION select * from t_media where film_alias like #{want}
) tmp order by film_release_year  desc

select * from ( select * from (
      select * from t_media where film_name like #{want}
UNION select * from t_media where film_stars like #{want}
UNION select * from t_media where film_director like #{want}
UNION select * from t_media where film_alias like #{want}
) tmp order by film_release_year  desc ) tttt
limit #{page_start}, 20
